import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';

export class Icon extends PureComponent {

    constructor(props){
        super(props);

        this.state = {
            colorActive: '#343434',
            colorInActive: '#C9C9C9'
        }
    }

    render() {
        let { src, epic, selected } = this.props;
        if(epic)
            src = selected ? src.replaceAll(this.state.colorActive, this.state.colorInActive) : src;

        return (
            <img src={src}/>
        )
    }
}

Icon.propTypes = {
    epic: PropTypes.bool,
    selected: PropTypes.bool,
};

export default Icon;
