import React from 'react';
import bridge from '@vkontakte/vk-bridge';
import '@vkontakte/vkui/dist/vkui.css';
import './css/Tracker.css'

import { Panel, PanelHeader, View, Epic, Tabbar, TabbarItem } from '@vkontakte/vkui';

import Icon28Truck from './icons/truck.svg';

class Tracker extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            popout: null,
            history: {
                add: [ 'add' ],
                history: [ 'history' ]
            },
            activePanel: {
                add: 'add',
                history: 'history'
            },
            activeStory: 'add'
        };

        this.componentDidMount = this.componentDidMount.bind(this);
        this.back = this.back.bind(this);
        this.go = this.go.bind(this);
        this.onStoryChange = this.onStoryChange.bind(this);
    }

    async componentDidMount () {
        bridge.send('VKWebAppInit');

        window.addEventListener('popstate', e => {
            e.preventDefault();
            this.back(e);
        });

        bridge.subscribe(async ({ detail: { type, data }}) => {
            if (type !== undefined) console.log(type, data);
            if (type === 'VKWebAppUpdateConfig') {
                const schemeAttribute = document.createAttribute('scheme');
                schemeAttribute.value = data.scheme ? data.scheme : 'bright_light';
                document.body.attributes.setNamedItem(schemeAttribute);
            }
        });
    }

    back = () => {
        if(this.state.popout !== null){
            this.setState({ popout: null });
            window.history.pushState({ pop: 'popout' }, 'Title');
            return ;
        }
        let { activePanel, activeStory, history } = this.state;
        let history_ = history[activeStory];
        if(history_.length === 1) {
            bridge.send('VKWebAppClose', { status: 'success' });
        } else if (history_.length > 1) {
            history_.pop();
            activePanel = history_[history_.length - 1];
            this.setState({ activePanel, history });
        }
    };
    go(panel) {
        let { activeStory, history } = this.state;
        let history_ = history[activeStory];
        if(history_[history_.length-1] !== panel) {
            history_.push(panel);
            window.history.pushState({ activePanel: panel }, 'Title');
            this.setState({ activePanel: panel, history });
        }
    }

    onStoryChange (e) {
        this.setState({ activeStory: e.currentTarget.dataset.story })
    }

    render() {
        return (
            <Epic activeStory={this.state.activeStory} tabbar={
                <Tabbar>
                    <TabbarItem
                        onClick={this.onStoryChange}
                        selected={this.state.activeStory === 'add'}
                        data-story='add'
                    ><Icon28Truck /></TabbarItem>
                </Tabbar>
            }>
                <View activePanel={this.state.activePanel.add} popout={this.state.popout} history={this.state.history.add} onSwipeBack={this.back}>
                    <Panel id='add'>
                        <PanelHeader>Отслеживание</PanelHeader>

                    </Panel>
                </View>
            </Epic>

        );
    }
}

export default Tracker;
