import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';
import '../css/Placeholder.css';
import MainPlaceholderIcon from "../icons/MainPlaceholderIcon.svg";
import MainPlaceholderButton from "../icons/MainPlaceholderButton.svg";
import {Input} from "@vkontakte/vkui";
import Icon28Clock from "../icons/Icon28Clock";

export class Placeholder extends PureComponent {

    render() {
        return (
            <placeholder>
                <top>
                    <description>{this.props.description}</description>
                    <title>
                        <img src={this.props.topIcon}/>
                        <text>{this.props.title}</text>
                    </title>
                </top>
                <bottom>
                    <btn>
                        <img src={this.props.btnIcon} />
                    </btn>
                    <Input placeholder={this.props.inputPlaceholder} onChange={this.props.inputOnChange}/>
                </bottom>
            </placeholder>
        )
    }
}

Placeholder.defaultProps = {
    inputOnChange: () => {}
};

Placeholder.propTypes = {
    title: PropTypes.string,
    description: PropTypes.string,
    topIcon: PropTypes.string,
    btnIcon: PropTypes.string,
    inputPlaceholder: PropTypes.string,
    inputOnChange: PropTypes.func
};

export default Placeholder;
