import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';
import {Avatar, SimpleCell} from "@vkontakte/vkui";
import '../css/TrackHistory.css';

import Icon34Box from '../icons/Icon34Box.svg';
import Icon34Truck from '../icons/Icon34Truck.svg';
import Icon34Message from '../icons/Icon34Message.svg';
import Icon34Airplane from '../icons/Icon34Airplane.svg';
import Icon34Customs from '../icons/Icon34Customs.svg';
import Icon34Building from '../icons/Icon34Building.svg';
import Icon34Customs2 from '../icons/Icon34Customs2.svg';
import Icon34Inbox from '../icons/Icon34Inbox.svg';
import Icon34Like from '../icons/Icon34Like.svg';
import Icon34Error from '../icons/Icon34Error.svg';

const status = [
    {
        icon: Icon34Box,
        text: 'Принято перевозчиком'
    }, {
        icon: Icon34Box,
        text: 'Прибыло на склад перевозчика'
    }, {
        icon: Icon34Truck,
        text: 'Покинуло склад'
    }, {
        icon: Icon34Message,
        text: 'Погрузка в самолёт'
    }, {
        icon: Icon34Airplane,
        text: 'Отправлено в страну назначения'
    }, {
        icon: Icon34Customs,
        text: 'Передано таможне'
    }, {
        icon: Icon34Building,
        text: 'Прибыло в сортировочный центр страны назначения'
    }, {
        icon: Icon34Customs2,
        text: 'Таможенное оформление'
    }, {
        icon: Icon34Inbox,
        text: 'Вручение получателю'
    }, {
        icon: Icon34Like,
        text: 'Выдано получателю'
    }, {
        icon: Icon34Error,
        text: 'Доставка не удалась'
    }, {
        icon: Icon34Error,
        text: 'Конфисковано таможней'
    }
];

let bodyRect, elemRect, offsetTop, offsetLeft;

export class TrackHistory extends PureComponent {

    componentDidMount() {
        bodyRect = document.body.getBoundingClientRect();
        elemRect = document.getElementById('thistory-avatar').getBoundingClientRect();
        offsetTop = elemRect.top - bodyRect.top;
        offsetLeft = elemRect.left - bodyRect.left;
        console.log(offsetTop, offsetLeft);
    }

    render() {
        console.log(offsetTop, offsetLeft);
        return (
            <div>
                {
                    this.props.history.reverse().map(( value, i ) => {
                        return <SimpleCell
                            className={ 'thistory-' + (i === 0 ? i : 1) }
                            disabled
                            style={{
                                margin: i === 0 ? '26px 40px 0 20px' : ( ( i === 1 ? '21px' : '30px' ) + ' 31px 0 92px' )
                            }}
                            before={
                                i === 0 &&
                                <Avatar id='thistory-avatar' size={80} shadow={false} >
                                    <img src={status[value.status].icon} />
                                </Avatar>
                            }
                            description={
                                ( i === 0 ? 'с ' : '' ) + value.date + ' · ' + value.country
                            }>
                            {
                                status[value.status].text
                            }
                        </SimpleCell>
                    })
                }
                <span style={{ position: 'absolute', top: Math.floor(offsetTop) + 'px', left: Math.floor(offsetLeft) + 'px' }} className='vertical-line'/>
            </div>
        )
    }
}

TrackHistory.defaultProps = {
    history: [],
};

TrackHistory.propTypes = {
    history: PropTypes.array,
};

export default TrackHistory;
