import React from 'react';
import bridge from '@vkontakte/vk-bridge';
import '@vkontakte/vkui/dist/vkui.css';
import './css/App.css'
import eruda from 'eruda';

import { Panel, Title, Counter, Headline, Footer, PanelHeader, View , SimpleCell, Slider, Switch, Div, ScreenSpinner, List , Cell , Gallery , FormLayout , Input , Avatar , Group , Epic, Tabbar, TabbarItem, Separator, Button, PanelHeaderContext, Header, PanelHeaderSimple, PanelHeaderContent, Placeholder, Tabs,
	TabsItem, PullToRefresh, Snackbar, FixedLayout, PanelHeaderBack, PanelHeaderClose, CellButton, PromoBanner, Link, platform, IOS, ANDROID  } from '@vkontakte/vkui';

import Icon16CancelCircleOutline from '@vkontakte/icons/dist/16/cancel_circle_outline';
import Icon16Dropdown from '@vkontakte/icons/dist/16/dropdown';
import Icon28AdvertisingOutline from '@vkontakte/icons/dist/28/advertising_outline';
import Icon28CameraOutline from '@vkontakte/icons/dist/28/camera_outline';
import Icon28GhostOutline from '@vkontakte/icons/dist/28/ghost_outline';
import Icon28StoryOutline from '@vkontakte/icons/dist/28/story_outline';

import Lottie from 'react-lottie'
import animationDataBackground from './animations/background.json'
import animationDataWaves from './animations/wave.json'
import animationDataLoading from './animations/loading2.json'
import {Upload} from "./components/Upload";

import html2canvas from 'html2canvas';

const blueBackground = {
	backgroundColor: 'var(--accent)'
}, redBackground = {
	backgroundColor: 'var(--dynamic_red)'
}, os = platform();


const random = require('random');

class App extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			popout: null,
			history: ["main"],
			activePanel: "main",

			show_native_ads: false,
			light_value: 0,
			light_interval_status: false,
			light_interval_time: 50,

			photo: 'https://sun1-83.userapi.com/c858020/v858020848/dabc4/pcnuQszWuCw.jpg',
			can_generate: true
		};

		this.componentDidMount = this.componentDidMount.bind(this);
		this.initializeApp = this.initializeApp.bind(this);
		this.back = this.back.bind(this);
		this.go = this.go.bind(this);
		this.change_hash = this.change_hash.bind(this);
		//eruda.init();
	}

	async componentDidMount () {

		let user = await bridge.sendPromise('VKWebAppGetUserInfo');
		this.setState({ photo: user.photo_max_orig, name: user.first_name + ' ' + user.last_name });

		window.addEventListener('popstate', e => {
			e.preventDefault();
			this.back(e);
		});

		bridge.subscribe(async ({ detail: { type, data }}) => {
			if(type !== undefined) console.log(type, data);
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = data.scheme ? data.scheme : 'client_light';
				document.body.attributes.setNamedItem(schemeAttribute);
			}
		});
		this.initializeApp();
	}

	initializeApp() {
		let hash = window.location.hash;
		if (hash) {
			hash = hash.replace('#','');
			this.go(hash);
		}else{
			this.change_hash('main');
		}
	}

	back = () => {
		if(this.state.popout !== null){
			this.setState({ popout: null });
			return ;
		}
		let { activePanel, history } = this.state;
		if(activePanel === 'main'){
			bridge.send("VKWebAppClose", {"status": "success"});
		}else {
			if(history.length === 1) {
				bridge.send("VKWebAppClose", {"status": "success"});
			} else if (history.length > 1) {
				history.pop();
				activePanel = history[history.length - 1];
				this.setState({activePanel});
				this.change_hash(activePanel);
			}
		}
	};
	go(panel) {
		let history = this.state.history;
		if(history[history.length-1] !== panel) {
			history.push(panel);
			window.history.pushState({activePanel: panel}, 'Title');
			this.setState({activePanel: panel, history, snackbar: null});
		}
		this.change_hash(panel);
	}
	change_hash (panel){
		bridge.send("VKWebAppSetLocation", {"location": panel});
	}

	async showSnackBar (text, icon, style, action, onActionClick){
		this.setState({ snackbar: null });
		await this.sleep(10);
		if(onActionClick === null) onActionClick = ()=> {};
		this.setState({ snackbar:
				<Snackbar
					layout="vertical"
					onClose={() => this.setState({ snackbar: null })}
					before={<Avatar size={24} style={style}>{icon}</Avatar>}
					action={action}
					onActionClick={onActionClick}
				>
					{text}
				</Snackbar>
		});
	}
	sleep(ms) {
		return new Promise(resolve => setTimeout(resolve, ms));
	}

	async uploadFile(element, evt) {
		let tgt = evt.target || window.event.srcElement,
			files = tgt.files;
		const t = this;

		if (FileReader && files && files.length) {
			for (let file of files) {
				let fr = new FileReader();
				fr.onload = async function () {
					let res = fr.result; //base64 result, to get src -> array[0].props.src
					if (file.type.includes('image')) {
						let image = new Image();
						image.src = res;
						image.onload = async function() {
							let canvas = document.createElement('CANVAS'),
								ctx = canvas.getContext('2d');
							canvas.height = 720;
							canvas.width = 720;
							ctx.drawImage(image, 0, 0, image.width, image.height, 0, 0, 720, 720);
							t.setState({ [element]: res, can_generate: true });

							this.setState({ loading: true });
							setTimeout(()=>{
								this.setState({ loading: false });
							}, 2200);
							let percents = 100;
							let p4 = random.int(1, 4); percents -= p4;
							let p3 = random.int(6, 13); percents -= p3;
							let p2 = random.int(25, 41); percents -= p2;
							let p1 = percents;

							let country = [
								[
									[ 'Россия', 'https://emojipedia-us.s3.dualstack.us-west-1.amazonaws.com/thumbs/72/apple/118/flag-for-russia_1f1f7-1f1fa.png' ],
									[ 'Украина', 'https://emojipedia-us.s3.dualstack.us-west-1.amazonaws.com/thumbs/72/apple/118/flag-for-ukraine_1f1fa-1f1e6.png' ],
