								]
							];
							let c1 = country[0][random.int(0, country[0].length-1)];
							let c2 = c1 === country[0][0] ? country[1][random.int(0, country[1].length-1)] : country[0][0];
							let c3 = country[2][random.int(0, country[2].length-1)];
							let c4 = country[3][random.int(0, country[3].length-1)];

							this.setState({ p1, p2, p3, p4, c1, c2, c3, c4, can_generate: false });
						}.bind(this);
					} else {
						t.showSnackBar('Необходимо загрузить изображение в формате PNG', <Icon16CancelCircleOutline fill='#fff' width={14} height={14} />, redBackground);

					}
				}.bind(this);
				await fr.readAsDataURL(file);
			}
		}
		// Not supported
		else {
			// fallback -- perhaps submit the input to an iframe and temporarily store
			// them on the server until the user's session ends.
		}
	}

	render() {
		return (
			<View activePanel={this.state.activePanel} popout={this.state.popout} history={this.state.history} onSwipeBack={this.back}>
				<Panel id='main'>
					<PanelHeader>Mini App</PanelHeader>
					<Group>
						<SimpleCell before={<Icon28AdvertisingOutline/>} onClick={()=>{ this.go('advert') }}>
							Реклама
						</SimpleCell>
						<SimpleCell before={<Icon28CameraOutline/>} onClick={()=>{ this.go('light') }}>
							Фонарик
						</SimpleCell>
						<SimpleCell before={<Icon28GhostOutline/>} onClick={()=>{ this.go('animation') }}>
							Анимация
						</SimpleCell>
					</Group>
				</Panel>
				<Panel id='advert'>
					<PanelHeader>Реклама</PanelHeader>
					<Cell indicator={os === ANDROID ? 'Android' : os === IOS ? 'IOS/Web' : os.toString()}>
						Платформа
					</Cell>
					<Group header={<Header mode='secondary'>Просмотр рекламы</Header>}>
						<Cell onClick={()=>{ this.setState({ show_banner_ads: !this.state.show_banner_ads }); }} asideContent={<Icon16Dropdown style={{ transform: `rotate(${this.state.show_banner_ads ? '180deg' : '0'})` }} />}>Баннер</Cell>
						{
							this.state.show_banner_ads &&
							<div>
								<div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: '8px' }}>
									<Button mode='primary' onClick={()=>{
										bridge.send('VKWebAppGetAds', {}).then(data => {
											this.setState({ promoBanner: <PromoBanner onClose={() => { this.setState({promoBanner: null}); }} bannerData={data}/> });
										});
									}}>Показать</Button>
									<Button style={{ marginLeft: '8px', marginRight: '8px' }} mode='secondary' onClick={()=>{ this.setState({promoBanner: null}); }}>Скрыть</Button>
								</div>
								<Separator style={{ marginTop: '8px' }}/>
							</div>
						}
						<Cell onClick={()=>{ if(os === ANDROID){ this.setState({ show_native_ads: !this.state.show_native_ads }) }else{ this.showSnackBar('Работает только на Android', <Icon16CancelCircleOutline fill={'#fff'}/>, redBackground) } }} asideContent={<Icon16Dropdown style={{ transform: `rotate(${this.state.show_native_ads ? '180deg' : '0'})` }} />}>Полноэкранная</Cell>
						{
							this.state.show_native_ads &&
								<div>
									<div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: '8px' }}>
										<Button mode='outline' onClick={()=>{ bridge.send('VKWebAppShowNativeAds', {ad_format:'preloader'}) }}>preloader</Button>
										<Button style={{ marginLeft: '8px' }}  mode='outline' onClick={()=>{ bridge.send('VKWebAppShowNativeAds', {ad_format:'reward'}) }}>reward</Button>
										<Button style={{ marginLeft: '8px', marginRight: '8px' }}  mode='outline' onClick={()=>{ bridge.send('VKWebAppShowNativeAds', {ad_format:'interstitial'}) }}>interstitial</Button>
									</div>
									<Separator style={{ marginTop: '8px' }}/>
								</div>
						}
					</Group>
					{
						this.state.promoBanner &&
						<FixedLayout vertical="bottom">
							{this.state.promoBanner}
						</FixedLayout>
					}
					{this.state.snackbar}
				</Panel>
				<Panel id='light'>
					<PanelHeader>Фонарик</PanelHeader>
					<FormLayout>
						<Slider
							min={0}
							max={1}
							value={Number(this.state.light_value)}
							onChange={async light_value => {
								this.setState({light_value});
								await bridge.send('VKWebAppFlashSetLevel', {level: light_value});
							}}
							top={String(this.state.light_value)}
						/>
						<Group header={<Header mode='secondary'>Интервал</Header>}>
							<Cell asideContent={<Switch checked={this.state.light_interval_status} onChange={ async () =>{
								if(this.state.light_value > 0) {
									await this.setState({light_interval_status: !this.state.light_interval_status});

									let interval = this.state.interval ? this.state.interval : null;
									try {clearInterval(interval);} catch (e) {}
									console.log(this.state.light_interval_status);
									if (this.state.light_interval_status) {
										interval = setInterval(async () => {
											if(this.state.light_interval_status) {
												await bridge.send('VKWebAppFlashSetLevel', {level: 0});
												await bridge.send('VKWebAppFlashSetLevel', {level: this.state.light_value});
											}
										}, this.state.light_interval_time);
									}
									this.setState({interval});
								}else{
									this.showSnackBar('Уровень яркости не должен быть равным нулю', <Icon16CancelCircleOutline fill={'#fff'}/>, redBackground);
								}
							}}/>}>
								Статус
							</Cell>
							<Input value={String(this.state.light_interval_time)} onChange={e => { if(e.target.value >= 50) this.setState({ light_interval_time: e.target.value }); else this.showSnackBar('Минимальное значение — 50', <Icon16CancelCircleOutline fill={'#fff'}/>, redBackground); }} type='number' top='Время (мс)'/>
						</Group>
						{this.state.snackbar}
					</FormLayout>
				</Panel>
				<Panel id='animation'>
					<Upload onChange={e => {this.uploadFile('photo', e)}}>
						<div style={{ background: 'white', height: '40vh' }}>
							<div style={{ paddingTop: 'calc(6vh + var(--safe-area-inset-top))' }}>
								<Avatar shadow={false} size={260} mode='app' src={this.state.photo} className='centered'/>
								{ false && <Title level='1' weight='medium' className='centered' style={{ marginTop: 16 }}>{this.state.name}</Title> }
							</div>
						</div>
					</Upload>
					<div style={{ borderTopLeftRadius: '5vh', borderTopRightRadius: '5vh', height: '60vh', background: '#1d1b26' }}>
						{
							this.state.loading ?
								<div className='absolute_centered'>
									<Lottie className='absolute_centered animated-loading' style={{ pointerEvents: 'none', zIndex: -1, transform: 'translateY(4vh)' }} options={{loop: true,
										autoplay: true,
										animationData: animationDataLoading,
										rendererSettings: {
											preserveAspectRatio: 'xMidYMid slice'
										}}} height={'100px'} width={'100px'}
									/>
								</div>
								:
								this.state.can_generate ?
									<Lottie className='absolute_centered' style={{ pointerEvents: 'none', zIndex: -1, transform: 'translateY(4vh)' }} options={{loop: true,
										autoplay: true,
										animationData: animationDataBackground,
										rendererSettings: {
											preserveAspectRatio: 'xMidYMid slice'
										}}} height={'45vh'}
									/>
									:
									<div className='star-background' style={{ width: '100vw', height: '60vh' }}/>
						}
						{
							!this.state.loading &&
							<div className='absolute_centered animated-loading' style={{ width: '100vw' }}>
								{
									this.state.can_generate ?
										<div>
											<Title style={{ color: 'white' }} level='3' className='centered' weight='medium'>Чтобы узнать, кто вы по национальности, загрузите фотографию, где хорошо видно ваше лицо</Title>
											<Upload onChange={e => {
												this.uploadFile('photo', e);
											}}>
												<div className='centered' style={{ marginTop: '16px' }}><Button mode='overlay_secondary'>Загрузить</Button></div>

											</Upload>
										</div>
										:
										<div style={{ position: 'absolute', top: '-5vh', left: '50%', transform: 'translateX(-50%)', width: '80vw' }}>
											<div className='centered'>
												<img src={this.state.c1[1]} height={32} />
												<SimpleCell style={{ height: '6vh', background: 'linear-gradient(90deg, #ffffff00, #e64646)', width: '70vw' }} disabled
															indicator={<Title level={2} weight='medium' style={{ color: 'white' }}>{this.state.p1}%</Title>}>
													<Title level={2} weight='medium' style={{ color: 'white', display: 'flex', alignItems: 'center' }}>
														{this.state.c1[0]}
													</Title>
												</SimpleCell>
											</div>
											<div className='centered' style={{ marginTop: '1.5vh' }}>
												<img src={this.state.c2[1]} height={32} />
												<SimpleCell style={{ height: '6vh', background: 'linear-gradient(90deg, #ffffff00, #e64646)', width: '70vw' }} disabled
															indicator={<Title level={2} weight='medium' style={{ color: 'white' }}>{this.state.p2}%</Title>}>
													<Title level={2} weight='medium' style={{ color: 'white', display: 'flex', alignItems: 'center' }}>
														{this.state.c2[0]}
													</Title>
												</SimpleCell>
											</div>
											<div className='centered' style={{ marginTop: '1.5vh' }}>
												<img src={this.state.c3[1]} height={32} />
												<SimpleCell style={{ height: '6vh', background: 'linear-gradient(90deg, #ffffff00, #e64646)', width: '70vw' }} disabled
															indicator={<Title level={2} weight='medium' style={{ color: 'white' }}>{this.state.p3}%</Title>}>
													<Title level={2} weight='medium' style={{ color: 'white', display: 'flex', alignItems: 'center' }}>
														{this.state.c3[0]}
													</Title>
												</SimpleCell>
											</div>
											<div className='centered' style={{ marginTop: '1.5vh' }}>
												<img src={this.state.c4[1]} height={32} />
												<SimpleCell style={{ height: '6vh', background: 'linear-gradient(90deg, #ffffff00, #e64646)', width: '70vw' }} disabled
															indicator={<Title level={2} weight='medium' style={{ color: 'white' }}>{this.state.p4}%</Title>}>
													<Title level={2} weight='medium' style={{ color: 'white', display: 'flex', alignItems: 'center' }}>
														{this.state.c4[0]}
													</Title>
												</SimpleCell>
											</div>

											<Footer><span style={{ color: 'white', opacity: .5 }}>Чтобы изменить фото, нажмите на свой аватар</span></Footer>
										</div>
								}
							</div>
						}
						{
							!this.state.loading && !this.state.can_generate &&
							<div style={{ position: 'absolute' , bottom: '1vh', width: '100vw', zIndex: 1 }}>
								<Button onClick={async ()=>{
									this.setState({ popout: <ScreenSpinner/>, screen: true });
									await this.sleep(250);
									let element = document.getElementsByClassName('View__panels')[0];
									html2canvas(element, { allowTaint: true }).then(async canvas => {
										let blob = canvas.toDataURL('image/png');
										try{
											let resp = await bridge.send('VKWebAppShowStoryBox', { background_type: 'image', blob, attachment: { url: 'https://vk.com/app' + 7439359, text: 'open', type: 'url' } });
											this.setState({ shared: true });
										}catch (e) {}
										this.setState({ popout: null, screen: false });
									});
								}} style={{ background: '#ffffff00', color: 'white', fontSize: '20px', letterSpacing: '1px' }} before={<Icon28StoryOutline/>} size='xl'>Поделиться в истории</Button>
							</div>
						}

						<Lottie style={{ position: 'absolute' , bottom: 0, pointerEvents: 'none' }} options={{loop: true,
							autoplay: true,
							animationData: animationDataWaves,
							rendererSettings: {
								preserveAspectRatio: 'xMidYMid slice'
							}}}
								height={'45vh'}
								width={'100vw'}
						/>
					</div>
				</Panel>
			</View>
		);
	}
}

export default App;
