import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';

export class Icon extends PureComponent {

    constructor(props){
        super(props);

        this.state = {
            colorActive: '#343434',
            colorInActive: '#C9C9C9'
        }
    }

    render() {
        let { src, epic, selected } = this.props;
        let element = <img src={src}/>;
        if(epic)
            element = selected ? <div dangerouslySetInnerHTML={{ __html: element }} /> : element;

        return (
            element
        )
    }
}

Icon.propTypes = {
    epic: PropTypes.bool,
    selected: PropTypes.bool,
};

export default Icon;
