import eruda from 'eruda';

import { Panel, Title, PanelHeader, View , SimpleCell, Slider, Switch, Div, ScreenSpinner, List , Cell , Gallery , FormLayout , Input , Avatar , Group , Epic, Tabbar, TabbarItem, Separator, Button, PanelHeaderContext, Header, PanelHeaderSimple, PanelHeaderContent, Placeholder, Tabs,
	TabsItem, PullToRefresh, Snackbar, FixedLayout, PanelHeaderBack, PanelHeaderClose, CellButton, PromoBanner, Link, platform, IOS, ANDROID  } from '@vkontakte/vkui';

import Icon16CancelCircleOutline from '@vkontakte/icons/dist/16/cancel_circle_outline';
import Icon16Dropdown from '@vkontakte/icons/dist/16/dropdown';
import Icon28AdvertisingOutline from '@vkontakte/icons/dist/28/advertising_outline';
import Icon28CameraOutline from '@vkontakte/icons/dist/28/camera_outline';
import Icon28GhostOutline from '@vkontakte/icons/dist/28/ghost_outline';

import Lottie from 'react-lottie'
import animationDataBackground from './animations/background.json'
import animationDataWaves from './animations/wave.json'

const blueBackground = {
	backgroundColor: 'var(--accent)'
}, redBackground = {
	backgroundColor: 'var(--dynamic_red)'
}, os = platform();


class App extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			popout: null,
			history: ["main"],
			activePanel: "main",

			show_native_ads: false,
			light_value: 0,
			light_interval_status: false,
			light_interval_time: 50,
		};

		this.componentDidMount = this.componentDidMount.bind(this);
		this.initializeApp = this.initializeApp.bind(this);
		this.back = this.back.bind(this);
		this.go = this.go.bind(this);
		this.change_hash = this.change_hash.bind(this);
		eruda.init();
	}

	componentDidMount () {
		window.addEventListener('popstate', e => {
			e.preventDefault();
			this.back(e);
		});

		bridge.subscribe(async ({ detail: { type, data }}) => {
			if(type !== undefined) console.log(type, data);
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = data.scheme ? data.scheme : 'client_light';
				document.body.attributes.setNamedItem(schemeAttribute);
			}
		});
		this.initializeApp();
	}

	initializeApp() {
		let hash = window.location.hash;
		if (hash) {
			hash = hash.replace('#','');
			this.go(hash);
		}else{
			this.change_hash('main');
		}
	}

	back = () => {
		if(this.state.popout !== null){
			this.setState({ popout: null });
			return ;
		}
		let { activePanel, history } = this.state;
		if(activePanel === 'main'){
			bridge.send("VKWebAppClose", {"status": "success"});
		}else {
			if(history.length === 1) {
				bridge.send("VKWebAppClose", {"status": "success"});
			} else if (history.length > 1) {
				history.pop();
				activePanel = history[history.length - 1];
				this.setState({activePanel});
				this.change_hash(activePanel);
			}
		}
	};
	go(panel) {
		let history = this.state.history;
		if(history[history.length-1] !== panel) {
			history.push(panel);
			window.history.pushState({activePanel: panel}, 'Title');
			this.setState({activePanel: panel, history, snackbar: null});
		}
		this.change_hash(panel);
	}
	change_hash (panel){
		bridge.send("VKWebAppSetLocation", {"location": panel});
	}

	async showSnackBar (text, icon, style, action, onActionClick){
		this.setState({ snackbar: null });
		await this.sleep(10);
		if(onActionClick === null) onActionClick = ()=> {};
		this.setState({ snackbar:
				<Snackbar
					layout="vertical"
					onClose={() => this.setState({ snackbar: null })}
					before={<Avatar size={24} style={style}>{icon}</Avatar>}
					action={action}
					onActionClick={onActionClick}
				>
					{text}
				</Snackbar>
		});
	}
	sleep(ms) {
		return new Promise(resolve => setTimeout(resolve, ms));
	}

	async uploadFile(element, evt) {
		let tgt = evt.target || window.event.srcElement,
			files = tgt.files;
		const t = this;

		if (FileReader && files && files.length) {
			for (let file of files) {
				let fr = new FileReader();
				fr.onload = async function () {
					let res = fr.result; //base64 result, to get src -> array[0].props.src
					let res1 = t.state.create_panel;
					if (file.type.includes('image')) {
						let image = new Image();
						image.src = res;
						image.onload = async function() {
							let canvas = document.createElement('CANVAS'),
								ctx = canvas.getContext('2d');
							canvas.height = 192;
							canvas.width = 192;
							ctx.drawImage(image, 0, 0, image.width, image.height, 0, 0, 192, 192);
							if (element === 'upload_sticker_img') {
								let res10 = res1.hasOwnProperty('uploaded_stickers_img') ? res1.uploaded_stickers_img : [];
								let stickers_count = this.clearArray(res10).length + 1;
								if (stickers_count <= 48) {
									res10.push(
										<StickerImg
											mode={'small'}
											id={'uploaded_sticker_' + res10.length}
											src={canvas.toDataURL("image/png")}
											onClick={(e) => {
												let id = parseInt(e.currentTarget.id.split('_')[2]);
												let res01 = t.state.create_panel;
												delete res01.uploaded_stickers_img[id];
												t.setState({create_panel: res01});
											}}
										/>
									);
									res1.uploaded_stickers_img = res10;
									t.setState({create_panel: res1});
								}
							} else {
								res1[element] = res;
								t.setState({create_panel: res1});
							}
						}.bind(this);
					} else {
						t.showSnackBar('Необходимо загрузить изображение в формате PNG', <Icon16Cancel fill="#fff" width={14} height={14} />, redBackground);
						res1[element] = null;
						t.setState({create_panel: res1});
					}
				}.bind(this);
				await fr.readAsDataURL(file);
			}
		}
		// Not supported
		else {
			// fallback -- perhaps submit the input to an iframe and temporarily store
			// them on the server until the user's session ends.
		}
	}

	render() {
		return (
			<View activePanel={this.state.activePanel} popout={this.state.popout} history={this.state.history} onSwipeBack={this.back}>
				<Panel id='main'>
					<PanelHeader>Mini App</PanelHeader>
					<Group>
						<SimpleCell before={<Icon28AdvertisingOutline/>} onClick={()=>{ this.go('advert') }}>
							Реклама
						</SimpleCell>
						<SimpleCell before={<Icon28CameraOutline/>} onClick={()=>{ this.go('light') }}>
							Фонарик
						</SimpleCell>
						<SimpleCell before={<Icon28GhostOutline/>} onClick={()=>{ this.go('animation') }}>
							Анимация
						</SimpleCell>
					</Group>
				</Panel>
				<Panel id='advert'>
					<PanelHeader>Реклама</PanelHeader>
					<Cell indicator={os === ANDROID ? 'Android' : os === IOS ? 'IOS/Web' : os.toString()}>
						Платформа
					</Cell>
					<Group header={<Header mode='secondary'>Просмотр рекламы</Header>}>
						<Cell onClick={()=>{ this.setState({ show_banner_ads: !this.state.show_banner_ads }); }} asideContent={<Icon16Dropdown style={{ transform: `rotate(${this.state.show_banner_ads ? '180deg' : '0'})` }} />}>Баннер</Cell>
						{
							this.state.show_banner_ads &&
							<div>
								<div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: '8px' }}>
									<Button mode='primary' onClick={()=>{
										bridge.send('VKWebAppGetAds', {}).then(data => {
											this.setState({ promoBanner: <PromoBanner onClose={() => { this.setState({promoBanner: null}); }} bannerData={data}/> });
										});
									}}>Показать</Button>
									<Button style={{ marginLeft: '8px', marginRight: '8px' }} mode='secondary' onClick={()=>{ this.setState({promoBanner: null}); }}>Скрыть</Button>
								</div>
								<Separator style={{ marginTop: '8px' }}/>
							</div>
						}
						<Cell onClick={()=>{ if(os === ANDROID){ this.setState({ show_native_ads: !this.state.show_native_ads }) }else{ this.showSnackBar('Работает только на Android', <Icon16CancelCircleOutline fill={'#fff'}/>, redBackground) } }} asideContent={<Icon16Dropdown style={{ transform: `rotate(${this.state.show_native_ads ? '180deg' : '0'})` }} />}>Полноэкранная</Cell>
						{
							this.state.show_native_ads &&
								<div>
									<div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: '8px' }}>
										<Button mode='outline' onClick={()=>{ bridge.send('VKWebAppShowNativeAds', {ad_format:'preloader'}) }}>preloader</Button>
										<Button style={{ marginLeft: '8px' }}  mode='outline' onClick={()=>{ bridge.send('VKWebAppShowNativeAds', {ad_format:'reward'}) }}>reward</Button>
										<Button style={{ marginLeft: '8px', marginRight: '8px' }}  mode='outline' onClick={()=>{ bridge.send('VKWebAppShowNativeAds', {ad_format:'interstitial'}) }}>interstitial</Button>
									</div>
									<Separator style={{ marginTop: '8px' }}/>
								</div>
						}
					</Group>
					{
						this.state.promoBanner &&
						<FixedLayout vertical="bottom">
							{this.state.promoBanner}
						</FixedLayout>
					}
					{this.state.snackbar}
				</Panel>
				<Panel id='light'>
					<PanelHeader>Фонарик</PanelHeader>
					<FormLayout>
						<Slider
							min={0}
							max={1}
							value={Number(this.state.light_value)}
							onChange={async light_value => {
								this.setState({light_value});
								await bridge.send('VKWebAppFlashSetLevel', {level: light_value});
							}}
							top={String(this.state.light_value)}
						/>
						<Group header={<Header mode='secondary'>Интервал</Header>}>
							<Cell asideContent={<Switch checked={this.state.light_interval_status} onChange={ async () =>{
								if(this.state.light_value > 0) {
									await this.setState({light_interval_status: !this.state.light_interval_status});

									let interval = this.state.interval ? this.state.interval : null;
									try {clearInterval(interval);} catch (e) {}
									console.log(this.state.light_interval_status);
									if (this.state.light_interval_status) {
										interval = setInterval(async () => {
											if(this.state.light_interval_status) {
												await bridge.send('VKWebAppFlashSetLevel', {level: 0});
												await bridge.send('VKWebAppFlashSetLevel', {level: this.state.light_value});
											}
										}, this.state.light_interval_time);
									}
									this.setState({interval});
								}else{
									this.showSnackBar('Уровень яркости не должен быть равным нулю', <Icon16CancelCircleOutline fill={'#fff'}/>, redBackground);
								}
							}}/>}>
								Статус
							</Cell>
							<Input value={String(this.state.light_interval_time)} onChange={e => { if(e.target.value >= 50) this.setState({ light_interval_time: e.target.value }); else this.showSnackBar('Минимальное значение — 50', <Icon16CancelCircleOutline fill={'#fff'}/>, redBackground); }} type='number' top='Время (мс)'/>
						</Group>
						{this.state.snackbar}
					</FormLayout>
				</Panel>
				<Panel id='animation'>
					<div style={{ background: 'white', height: '30vh' }}>
						<div style={{ paddingTop: 'calc(6vh + var(--safe-area-inset-top))' }}>
							<Avatar shadow={false} size={92} src={'https://sun1-83.userapi.com/c858020/v858020848/dabc4/pcnuQszWuCw.jpg'} className='centered'/>
							<Title level='2' weight='medium' className='centered' style={{ marginTop: 16 }}>Илья Ульянов</Title>
						</div>
					</div>
					<div className='animated-background' style={{ borderTopLeftRadius: '5vh', borderTopRightRadius: '5vh', height: '70vh' }}>
						<Lottie className='absolute_centered' style={{ pointerEvents: 'none', zIndex: -1, transform: 'translateY(4vh)' }} options={{loop: true,
							autoplay: true,
							animationData: animationDataBackground,
							rendererSettings: {
								preserveAspectRatio: 'xMidYMid slice'
							}}} height={'45vh'}
						/>
						<div className='absolute_centered' style={{ width: '100vw', transform: 'translate(-50%, 80%)' }}>
							<Title style={{ color: 'white' }} level='3' className='centered' weight='regular'>Узнайте, кто Вы национальности</Title>
							<div className='centered' style={{ marginTop: '16px' }}><Button mode='overlay_secondary'>Поехали!</Button></div>
						</div>
						<Lottie style={{ position: 'absolute' , bottom: 0, pointerEvents: 'none' }} options={{loop: true,
							autoplay: true,
							animationData: animationDataWaves,
							rendererSettings: {
								preserveAspectRatio: 'xMidYMid slice'
							}}}
								height={'45vh'}
								width={'100vw'}
						/>
					</div>
				</Panel>
			</View>
		);
	}
}

export default App;
