import React, { PureComponent } from 'react';
import '@vkontakte/vkui/dist/vkui.css';

export class Upload extends PureComponent {

    render() {
        let children = this.props.children;
        children.onClick = ()=> { this.upload__.click(); };
        return (
            <div>
                <input accept='.png' type='file' multiple={false} ref={(ref) => { this.upload__ = ref; }} style={{display: 'none'}} onChange={this.props.onChange}/>
                {child}
            </div>
        )
    }

}
