import React, { PureComponent } from 'react';
import PropTypes from 'prop-types';
import '../css/Placeholder.css';
import '../css/App.css';
import {FormLayout, Input} from "@vkontakte/vkui";

export class Placeholder extends PureComponent {

    constructor(props){
        super(props);

        this.state = {
            inputValue: ''
        }
    }

    render() {
        return (
            <placeholder>
                <top>
                    <description>{this.props.description}</description>
                    <title>
                        <img src={this.props.topIcon}/>
                        <text>{this.props.title}</text>
                    </title>
                </top>
                <bottom>
                    <FormLayout>
                        {
                            this.state.inputValue.length > 0 &&
                            <btn className='animated-loading'>
                                <img src={this.props.btnIcon} onClick={async (e)=>{
                                    let target = e.currentTarget;
                                    target.style.pointerEvents = 'none';

                                    if(this.props.btnIconLoading.length > 0) target.src = this.props.btnIconLoading;
                                    await this.props.btnOnClick(e);
                                    if(this.props.btnIconLoading.length > 0) target.src = this.props.btnIcon;

                                    target.style.pointerEvents = 'auto';
                                }}/>
                            </btn>
                        }
                        <Input style={{ width: this.state.inputValue.length === 0 ? '324px' : '262px' }} placeholder={this.props.inputPlaceholder} onChange={async (e)=> {
                            this.setState({ inputValue: e.currentTarget.value });
                            await this.props.inputOnChange(e);
                        }}/>
                    </FormLayout>
                </bottom>
            </placeholder>
        )
    }
}

Placeholder.defaultProps = {
    btnIconLoading: '',
    btnOnClick: (e) => {},
    inputOnChange: (e) => {}
};

Placeholder.propTypes = {
    title: PropTypes.string,
    description: PropTypes.string,
    topIcon: PropTypes.string,
    btnIcon: PropTypes.string,
    btnIconLoading: PropTypes.string,
    inputPlaceholder: PropTypes.string,
    inputOnChange: PropTypes.func,
    btnOnClick: PropTypes.func
};

export default Placeholder;
